public class Ej21 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// calcular la suma de pares e impares de los numeros entre 1 y 1000 utilizando un switch
		
		int sp=0;
		int si=0;
		int sw;
		
		for (int i=1; i<=1000; i++) {
			switch (sw=i%2) {
			case 0:
				sp=sp+i;
				break;
			case 1:
				si=si+i;
				break;
			}			
		}
		System.out.println("La suma de los pares es " + sp);
		System.out.println("La suma de los impares es " + si);		

	}

}
