public class Ej5 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// mostrar nºs impares del 1 al 100 y cuantos hay
		int ci=0;
		for (int i=1; i<=100; i=i+2) {
			ci++;
			System.out.println(i);
	}
		System.out.println(ci);
	}

}
